import { createSlice } from "@reduxjs/toolkit";

export const selectOnShowState = state => state

const todoListShowSlice = createSlice({
    name: "todoListShow",
    initialState: {
        all: true,
        active: false,
        completed: false
    },
    reducers: {
        onAll: state => {
            state = {all: true, active: false, completed: false};
        },
        onActive: state => {
            state = {all: false, active: true, completed: false};
        },
        onCompleted: state => {
            state = {all: false, active: false, completed: true}
        }
    }
})

export const { onAll, onActive, onCompleted }= todoListShowSlice.actions;
export default todoListShowSlice.reducer;
