import Box from '../../images/circle-regular.svg';
import { addTodo } from '../Todo/todoSlice'; 
import { useDispatch, useSelector } from 'react-redux';
import { selectGetInputSlice, changeInput } from '../Todo/todoAutoTakeInputSlice';

export const TodoInput = () => {
    const text = useSelector(selectGetInputSlice);
    const dispatch = useDispatch();
    let idCounter = 0;

    const createTodo = () => { //sent todo to the store for rendering to mission later 
        dispatch(addTodo({
            text: text, // using another store to store the onchange value 
            done: false,
            id: idCounter + 1
        }));
        idCounter += 1;
    }


    return (
        <div className="todo-input todo-check">
            <img className='button-check' src={Box} alt='box'/>
            <input className='mission' 
            type="text" placeholder="Create a new todo..."
            
            onChange={({target}) => {dispatch(changeInput(target.value))}}
            onKeyDown={e => {
                if (e.code === "Enter") {  //checks whether the pressed key is "Enter"
                    createTodo();
                }
            }} />
        </div>
    )
}
