import { createSlice } from "@reduxjs/toolkit";

export const selectGetInputSlice = state => state.todoAutoTakeInput.input; 

const todoAutoTakeInputSlice = createSlice({
    name: "getInputSlice",
    initialState: { input: "" },
    reducers: {
        changeInput: (state, action) => {
            state.input = action.payload.input;
        }
    }
})

export const { changeInput } = todoAutoTakeInputSlice.actions;
export default todoAutoTakeInputSlice.reducer;
